CREATE TABLE "Todos" (
  "id" INTEGER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
  "name" VARCHAR(255)
);

CREATE TABLE "Activities" (
  "id" INTEGER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
  "name" VARCHAR(255),
  "duration" INTEGER,
  "description" VARCHAR(255),
  "todo_id" INTEGER
);

ALTER TABLE "Activities" ADD FOREIGN KEY ("todo_id") REFERENCES "Todos" ("id");
